-- Import Style Filters with variant support: which = 'primary' (default) or 'alt'
function NS.ApplyOnlyPlatesStyleFiltersVariant(which)
	local engine = _G and rawget(_G, "ElvUI")
	if not (engine and engine[1]) then return false end
	local E = engine[1]
	local D = E and E:GetModule('Distributor')
	if not D or not D.ImportProfile then return false end
	local only = NS.OnlyPlatesStrings
	which = (which == 'alt') and 'alt' or 'primary'

	-- Pick the correct export string for the chosen variant
	local exportStr
	if which == 'alt' then
		exportStr = only and only.nameplatefilters_alt
	else
		exportStr = only and only.nameplatefilters
	end
	if not (exportStr and exportStr ~= "") then return false end

	-- Import new style filters
	D:ImportProfile(exportStr)

	-- Mark which variant was last applied (no cleanup behavior attached)
	E.global = E.global or {}
	E.global.TrenchyUI = E.global.TrenchyUI or {}
	E.global.TrenchyUI.styleFiltersActive = which

	-- Ensure Nameplates module is enabled in ElvUI config
	E.private = E.private or {}; E.private.nameplate = E.private.nameplate or {}
	E.private.nameplate.enable = true

	return true
end

-- Back-compat: primary variant wrapper used throughout the installer/config today
function NS.ApplyOnlyPlatesStyleFilters()
	return NS.ApplyOnlyPlatesStyleFiltersVariant('primary')
end

-- Convenience wrapper for the alternate option
function NS.ApplyOnlyPlatesStyleFilters_Alt()
	return NS.ApplyOnlyPlatesStyleFiltersVariant('alt')
end

-- Import just the Aura Filters (used for custom aura filtering)
function NS.ApplyOnlyPlatesAuraFilters()
	local engine = _G and rawget(_G, "ElvUI")
	if not (engine and engine[1]) then return false end
	local E = engine[1]
	local D = E and E:GetModule('Distributor')
	if not D or not D.ImportProfile then return false end
	local only = NS.OnlyPlatesStrings
	if not (only and only.aurafilters and only.aurafilters ~= "") then return false end
	D:ImportProfile(only.aurafilters)
	return true
end

-- Optional: tweaks after import to enforce Expressway and your textures
function NS.OnlyPlates_PostImport()
	local engine = _G and rawget(_G, "ElvUI")
	if not (engine and engine[1]) then return end
	local E = engine[1]

	-- Fonts / media
	local expressway = E and E.media and E.media.normFont
	if expressway then
		E.db = E.db or {}
		E.db.general = E.db.general or {}
		E.db.general.font = expressway
	end

	-- Example: statusbar default for unitframes
	local trenchyStatus = "TrenchyBlank" -- Registered in Media/SharedMedia.lua
	if E.db and E.db.unitframe then
		E.db.unitframe.statusbar = trenchyStatus
	end
end
